import React, { useState, useEffect} from 'react';
import './App.css';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { Line } from 'react-chartjs-2';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

export const options = {
  responsive: true,
  plugins: {
    legend: {
      position: 'top',
    },
    title: {
      display: true,
      text: 'Chart.js Line Chart',
    },
  },
};

function App() {
  const [selected, setSelected] = useState("default"); // selected data type
  const [data, setData] = useState(); // chart data

  return (
    <div className="App">
      <header className="App-header">
        <p>Olympic Industries Lumber Futures Data Visualization</p>
        <select id="dataType" className='dataTypeDropdown' onChange={
          (e) => {
            setSelected(e.target.value);
            fetch(`/data?type=${e.target.value}`)
              .then(res => res.json())
              .then(data => {
                setData({
                  labels: data.allData.map(d => d.date),
                  datasets: [
                    {
                      label: e.target.value,
                      data: data.allData.map(d => d.price),
                      fill: false,
                      backgroundColor: 'rgb(75, 192, 192)',
                      borderColor: 'rgba(75, 192, 192, 0.2)',
                    },
                  ],
                });
              })
              .catch(err => console.log(err));
          }
        } defaultValue="default">
          <option value="default" disabled>Select Value</option>
          <option value="Open">Open</option>
          <option value="High">High</option>
          <option value="Low">Low</option>
          <option value="Close">Close</option>
          <option value="AdjClose">AdjClose</option>
          <option value="Volume">Volume</option>
        </select>
        {/* <Line data={data} options={options} /> */}
      </header>
    </div>
  );
}

export default App;
