import React, { useState, useEffect} from 'react';
import './App.css';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { Line } from 'react-chartjs-2';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

export const options = {
  responsive: true,
  plugins: {
    legend: {
      position: 'top',
    },
    title: {
      display: true,
      text: 'Chart.js Line Chart',
    },
  },
};

function App() {
  // const [selected, setSelected] = useState("default"); // selected data type
  // const [lumberData, setLumberData] = useState(); // chart data
  // const [chartData, setChartData] = useState(); // chart data
  var lumberData = {};
  var selected = "default";

  return (
    <div className="App">
      <header className="App-header">
        <p>Olympic Industries Lumber Futures Data Visualization</p>
        <select id="dataType" className='dataTypeDropdown' onChange={
          (e) => {
            selected = e.target.value;
            const test = {};
            fetch(`/data?type=${e.target.value}`)
              .then(res => res.json())
              .then(data => {
                lumberData = data.allData;
            }).catch(err => console.log(err));
            console.log(JSON.stringify(lumberData));
          }
        } defaultValue="default">
          <option value="default" disabled>Select Value</option>
          <option value="Open">Open</option>
          <option value="High">High</option>
          <option value="Low">Low</option>
          <option value="Close">Close</option>
          <option value="AdjClose">AdjClose</option>
          <option value="Volume">Volume</option>
        </select>
      </header>
    </div>
  );
}

export default App;
