import React, { useState, useEffect} from 'react';
import './App.css';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

import faker from 'faker';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

function App() {
  // const [data, setData] = React.useState(null);
  // React.useEffect(() => {
  //   fetch("/data?type=Open")
  //     .then(res => res.json())
  //     .then(data => setData(data.allData));
  // }, []);
  // console.log(data);

  const [data, setData] = useState();
  useEffect(() => {
    fetch("/data?type=Open")
      .then(res => res.json())
      .then(data => {
        setData(data.allData)
      })
      .catch(err => console.log(err));
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <a>Olympic Industries Lumber Futures Data Visualization</a>
        <select id="dataType" className='dataTypeDropdown'>
          <option value="Open">Open</option>
          <option value="High">High</option>
          <option value="Low">Low</option>
          <option value="Close">Close</option>
          <option value="AdjClose">AdjClose</option>
          <option value="Volume">Volume</option>
        </select>
      </header>
    </div>
  );
}

export default App;
