import React, { useState, useEffect} from 'react';
import './App.css';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
// import { Line } from 'react-chartjs-2';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

export const options = {
  responsive: true,
  plugins: {
    legend: {
      position: 'top',
    },
    title: {
      display: true,
      text: 'Chart.js Line Chart',
    },
  },
};

function App() {
  const [selected, setSelected] = useState("Open");
  const [data, setData] = useState();

  return (
    <div className="App">
      <header className="App-header">
        <p>Olympic Industries Lumber Futures Data Visualization</p>
        <select id="dataType" className='dataTypeDropdown' onChange={
          (e) => {
            setSelected(e.target.value);
            fetch(`/data?type=${e.target.value}`)
              .then(res => res.json())
              .then(data => {
                setData(data.allData)
              })
              .catch(err => console.log(err));
          }
        } defaultValue="Select Value">
          {console.log(String(selected))}
          {console.log(JSON.stringify(data))}
          <option value="Open">Open</option>
          <option value="High">High</option>
          <option value="Low">Low</option>
          <option value="Close">Close</option>
          <option value="AdjClose">AdjClose</option>
          <option value="Volume">Volume</option>
        </select>
      </header>
    </div>
  );
}

export default App;
